<div class="modal fade bd-example-modal-lg" tabindex="-1"aria-hidden="true"  id="messages">
    <div class="modal-dialog modal-lg">
        <div class="modal-content">
            <div class="modal-header bg-light">
                <h4 class="modal-title">Error </h4>
                <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                    <span aria-hidden="true">&times;</span>
                </button>
            </div>
            <div class="modal-body bg-white text-mutted">
                <div class="text-danger"  id="activeError" style="display:none;">
                    <h2>Error: </h2>
                    <div id="msg-error"></div>
                </div>  
                <div class="text-success" id="activeSuccess" style="display:none;">
                    <h2>Success: </h2>
                    <div id="msg-success"></div>
                </div>
            </div>
        </div>
    </div>
</div>